def encode(string):
    encode_string = ""
    for char in string:
        binary = (bin(ord(char))[2:]).zfill(8)
        for digit in binary:
            encode_string += digit * 3
    return str(encode_string)

def decode(string):
    decode_bytes = ""
    triples = [string[i:i + 3] for i in range(0, len(string), 3)]
    for triple in triples:
        corrected_bits = '1' if triple.count('1') >= 2 else '0'
        decode_bytes += corrected_bits
    bytes_string = [decode_bytes[i:i + 8] for i in range(0, len(decode_bytes), 8)]
    decoded_string = ""
    for byte in bytes_string:
        ascii_char = chr(int(byte, 2))
        decoded_string += ascii_char
    return decoded_string

if __name__ == "__main__":
    print(encode("hey"))
    print(decode(""))